list-view-config.query-select:

PREFIX afn:  <http://jena.hpl.hp.com/ARQ/function#> 
PREFIX core: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX vcard: <http://www.w3.org/2006/vcard/ns#>

SELECT ?vcard ?link 
       (afn:localname(?link) AS ?linkName) 
       ?label 
       ?url 
       ?rank WHERE {
    ?subject ?property ?vcard .
    ?vcard vcard:hasURL ?link
    OPTIONAL { ?link rdfs:label ?label }
    OPTIONAL { ?link vcard:url ?url }
    OPTIONAL { ?link core:rank ?rank }

FILTER ( bound(?link) )    

} ORDER BY ?rank ?label

list-view-config.query-construct:

PREFIX core: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX vcard: <http://www.w3.org/2006/vcard/ns#>
CONSTRUCT {
    ?subject ?property ?vcard .
    ?vcard vcard:hasURL ?link .
    ?link vcard:url ?url .
    ?link core:rank ?rank .
    ?link rdfs:label ?label
} WHERE {
    { 
        ?subject ?property ?vcard .
        ?vcard vcard:hasURL ?link
    } UNION {
        ?subject ?property ?vcard .
        ?vcard vcard:hasURL ?link .
        ?link vcard:url ?url 
    } UNION {
        ?subject ?property ?vcard .
        ?vcard vcard:hasURL ?link .
        ?link core:rank ?rank
    } UNION {
        ?subject ?property ?vcard .
        ?vcard vcard:hasURL ?link .
        ?link rdfs:label ?label
    } 
}
